    def __init__(self, value):
        self.value = value
        self.left = None
        self.right = None

class BinaryTree:
    def __init__(self):
        self.root = None

    def insert(self, value):
        """Inserts a new value into the binary search tree."""
        if not self.root:
            self.root = TreeNode(value)
        else:
            self._insert_recursively(self.root, value)

    def _insert_recursively(self, node, value):
        """Helper method to insert a value recursively."""
        if value < node.value:
            if node.left is None:
                node.left = TreeNode(value)
            else:
                self._insert_recursively(node.left, value)
        else:
            if node.right is None:
                node.right = TreeNode(value)
            else:
                self._insert_recursively(node.right, value)

    def in_order_traversal(self):
        """Performs an in-order traversal of the tree and returns the values in a list."""
        elements = []
        self._in_order_traversal_recursively(self.root, elements)
        return elements

    def _in_order_traversal_recursively(self, node, elements):
        """Helper method to perform in-order traversal recursively."""
        if node:
            self._in_order_traversal_recursively(node.left, elements)
            elements.append(node.value)
            self._in_order_traversal_recursively(node.right, elements)

    def find_minimum(self):
        """Finds and returns the minimum value in the tree."""
        if not self.root:
            return None
        current = self.root
        while current.left:
            current = current.left
        return current.value

