        if title:
            return {title: self.inventory[title]} if title in self.inventory else {}
        elif author:
            return {k: v for k, v in self.inventory.items() if v[0] == author}
        else:
            raise ValueError("Either title or author must be provided for search.")

    def update_stock(self, title, stock_count):
        """Update the stock count of a book. Raise KeyError if the book does not exist."""
        if title not in self.inventory:
            raise KeyError("Book not found in inventory.")
        author, _ = self.inventory[title]
        self.inventory[title] = (author, stock_count)

    def list_books(self):
