from collections import Counter

def can_split_text(text, word_count):
    """
    Determines if the given text can be split into a sequence of words from the word_count dictionary
    such that each word's frequency in the sequence does not exceed its frequency in the dictionary.

    :param text: The string to be split.
    :param word_count: A dictionary with words as keys and their frequencies as values.
    :return: True if the text can be split as described, False otherwise.
    """
    def dfs(remaining_text, word_counter):
        if not remaining_text:
            return True
        for word in word_counter:
            if remaining_text.startswith(word) and word_counter[word] > 0:
                word_counter[word] -= 1
                if dfs(remaining_text[len(word):], word_counter):
                    return True
                word_counter[word] += 1
        return False

    word_counter = Counter(word_count)
    return dfs(text, word_counter)
