class Matrix:
    def __init__(self, data):
        """
        Initializes the Matrix with a list of lists.
        :param data: List of lists representing the matrix rows.
        :raises ValueError: If all rows are not of the same length.
        """
        if not all(isinstance(row, list) and len(row) == len(data[0]) for row in data):
            raise ValueError("All rows must be lists of the same length.")
        self.data = data

    def add(self, other):
        """
        Adds two matrices.
        :param other: Another Matrix instance.
        :return: A new Matrix instance representing the sum.
        :raises ValueError: If matrices do not have the same dimensions.
        """
        if not isinstance(other, Matrix):
            raise ValueError("The operand must be an instance of Matrix.")
        if self.get_dimensions() != other.get_dimensions():
            raise ValueError("Matrices must have the same dimensions to be added.")

        result = [
            [self.data[i][j] + other.data[i][j] for j in range(len(self.data[0]))]
            for i in range(len(self.data))
        ]
        return Matrix(result)

    def subtract(self, other):
        """
        Subtracts one matrix from another.
        :param other: Another Matrix instance.
        :return: A new Matrix instance representing the difference.
        :raises ValueError: If matrices do not have the same dimensions.
        """
        if not isinstance(other, Matrix):
            raise ValueError("The operand must be an instance of Matrix.")
        if self.get_dimensions() != other.get_dimensions():
            raise ValueError("Matrices must have the same dimensions to be subtracted.")

        result = [
            [self.data[i][j] - other.data[i][j] for j in range(len(self.data[0]))]
            for i in range(len(self.data))
        ]
        return Matrix(result)

    def multiply(self, other):
        """
        Multiplies two matrices.
        :param other: Another Matrix instance.
        :return: A new Matrix instance representing the product.
        :raises ValueError: If the number of columns in the first matrix is not equal to the number of rows in the second matrix.
        """
        if not isinstance(other, Matrix):
            raise ValueError("The operand must be an instance of Matrix.")
        if len(self.data[0]) != len(other.data):
            raise ValueError("Number of columns in the first matrix must be equal to the number of rows in the second matrix for multiplication.")

        result = [
            [sum(self.data[i][k] * other.data[k][j] for k in range(len(other.data)))
             for j in range(len(other.data[0]))]
            for i in range(len(self.data))
        ]
        return Matrix(result)

    def get_dimensions(self):
        """
        Returns the dimensions of the matrix.
        :return: A tuple (rows, columns).
        """
        return len(self.data), len(self.data[0])

    def __str__(self):
        """
        Returns a string representation of the matrix.
        :return: A string with rows separated by newlines and elements separated by spaces.
        """
        return '\n'.join([' '.join(map(str, row)) for row in self.data])
