def average_salary_by_department(employees):
    """
    Computes the average salary for each department from the given employees dictionary.

    Parameters:
    employees (dict): A dictionary where keys are employee IDs and values are dictionaries
                      containing employee information including 'name', 'department', and 'salary'.

    Returns:
    dict: A dictionary with department names as keys and their average salaries as values.
    """
    department_salaries = {}

    for emp_id, emp_info in employees.items():
        department = emp_info['department']
        salary = emp_info['salary']

        if department not in department_salaries:
            department_salaries[department] = {'total_salary': 0, 'count': 0}

        department_salaries[department]['total_salary'] += salary
        department_salaries[department]['count'] += 1

    average_salaries = {
        department: info['total_salary'] / info['count']
        for department, info in department_salaries.items()
    }

    return average_salaries

