import pandas as pd
from sklearn.preprocessing import MinMaxScaler

class DataProcessor:
    def __init__(self):
        self.data = None

    def load_data(self, filepath):
        """Read data from a CSV file and store it as a pandas DataFrame."""
        try:
            self.data = pd.read_csv(filepath)
        except FileNotFoundError:
            print(f"Error: The file {filepath} was not found.")
        except pd.errors.EmptyDataError:
            print(f"Error: The file {filepath} is empty.")
        except pd.errors.ParserError:
            print(f"Error: The file {filepath} could not be parsed.")

    def normalize_data(self, columns):
        """Normalize specified columns using Min-Max scaling and return the normalized DataFrame."""
        if self.data is None:
            raise ValueError("Data not loaded. Please load data using load_data(filepath).")

        if not set(columns).issubset(self.data.columns):
            raise ValueError("One or more specified columns do not exist in the dataset.")

        scaler = MinMaxScaler()
        self.data[columns] = scaler.fit_transform(self.data[columns])
        return self.data

    def handle_missing_data(self):
        """Fill missing values with the median of the respective columns and return the DataFrame."""
        if self.data is None:
            raise ValueError("Data not loaded. Please load data using load_data(filepath).")

        self.data.fillna(self.data.median(), inplace=True)
        return self.data
