def count_pairs(nums, target):
    """
    Returns the number of pairs in the list that add up to the given target value.

    :param nums: List of integers
    :param target: Integer target sum
    :return: Integer count of pairs
    """
    num_counts = {}
    pair_count = 0

    for num in nums:
        complement = target - num
        if complement in num_counts:
            pair_count += num_counts[complement]
        if num in num_counts:
            num_counts[num] += 1
        else:
            num_counts[num] = 1

    return pair_count
