class TreeNode:
    def __init__(self, key):
        self.left = None
        self.right = None
        self.value = key

class BinarySearchTree:
    def __init__(self):
        self.root = None

    def insert(self, key):
        """Inserts a new node with the given key into the BST."""
        if self.root is None:
            self.root = TreeNode(key)
        else:
            self._insert_rec(self.root, key)

    def _insert_rec(self, node, key):
        """Recursively inserts a new node with the given key into the BST."""
        if key < node.value:
            if node.left is None:
                node.left = TreeNode(key)
            else:
                self._insert_rec(node.left, key)
        else:
            if node.right is None:
                node.right = TreeNode(key)
            else:
                self._insert_rec(node.right, key)

    def inorder_traversal(self):
        """Performs an in-order traversal of the BST and returns the node values in a list."""
        return self._inorder_traversal_rec(self.root)

    def _inorder_traversal_rec(self, node):
        """Recursively performs an in-order traversal of the BST."""
        res = []
        if node is not None:
            res = self._inorder_traversal_rec(node.left)
            res.append(node.value)
            res = res + self._inorder_traversal_rec(node.right)
        return res

