import random

def is_valid(board, row, col, num):
    """
    Check if placing `num` at position (row, col) in the board is valid.
    """
    for i in range(9):
        if board[row][i] == num or board[i][col] == num:
            return False

    start_row, start_col = 3 * (row // 3), 3 * (col // 3)
    for i in range(3):
        for j in range(3):
            if board[i + start_row][j + start_col] == num:
                return False
    return True

def solve_sudoku(board):
    """
    Solve the Sudoku puzzle using backtracking.
    """
    empty = find_empty_location(board)
    if not empty:
        return True  # Puzzle solved
    row, col = empty

    for num in range(1, 10):
        if is_valid(board, row, col, num):
            board[row][col] = num
            if solve_sudoku(board):
                return True
            board[row][col] = 0  # Backtrack
    return False

def find_empty_location(board):
    """
    Find an empty location (represented by 0) in the board.
    """
    for i in range(9):
        for j in range(9):
            if board[i][j] == 0:
                return (i, j)
    return None

def count_solutions(board, row_col):
    """
    Count the number of solutions for the Sudoku puzzle starting from position (row, col).
    """
    row, col = row_col
    num_solutions = 0
    for num in range(1, 10):
        if is_valid(board, row, col, num):
            board[row][col] = num
            if not find_empty_location(board):
                num_solutions += 1
            else:
                num_solutions += count_solutions(board, find_empty_location(board))
            board[row][col] = 0  # Backtrack
    return num_solutions

def generate_sudoku():
    """
    Generate a valid 9x9 Sudoku puzzle with a unique solution.
    """
    while True:
        # Create a solved Sudoku
        board = [[0] * 9 for _ in range(9)]
        numbers = list(range(1, 10))

        for i in range(9):
            random.shuffle(numbers)
            for j in range(9):
                if is_valid(board, i, j, numbers[j]):
                    board[i][j] = numbers[j]
                    break
            else:
                break  # If no valid number is found, restart
        else:
            break  # If the loop completes, the board is fully filled

        # Create the puzzle by removing numbers
        attempts = 5
        while attempts > 0:
            row, col = random.randint(0, 8), random.randint(0, 8)
            while board[row][col] == 0:
                row, col = random.randint(0, 8), random.randint(0, 8)
            num = board[row][col]
            board[row][col] = 0

            # Make a copy of the board to count the number of solutions
            temp_board = [row[:] for row in board]

            if find_empty_location(temp_board):
                num_solutions = count_solutions(temp_board, (row, col))
                if num_solutions != 1:
                    board[row][col] = num
                    attempts -= 1
        return board
