def is_dag(graph):
    """
    Determines if a directed graph is a Directed Acyclic Graph (DAG).

    :param graph: A dictionary representing the graph as an adjacency list.
    :return: True if the graph is a DAG, False otherwise.
    """
    visited = set()
    rec_stack = set()

    def dfs(v):
        visited.add(v)
        rec_stack.add(v)

        for neighbour in graph.get(v, []):
            if neighbour not in visited:
                if dfs(neighbour):
                    return True
            elif neighbour in rec_stack:
                return True

        rec_stack.remove(v)
        return False

    for node in graph:
        if node not in visited:
            if dfs(node):
                return False
    return True
