def extract_config_values(config, key):
    """
    Recursively extracts all values associated with a given key from a nested JSON configuration.

    :param config: A nested dictionary or list representing the JSON configuration.
    :param key: A string representing the key whose values need to be extracted.
    :return: A list of values associated with the given key.
    """
    def recursive_search(obj, key):
        results = []
        if isinstance(obj, dict):
            for k, v in obj.items():
                if k == key:
                    results.append(v)
                results.extend(recursive_search(v, key))
        elif isinstance(obj, list):
            for item in obj:
                results.extend(recursive_search(item, key))
        return results

