class UnionFind:
    def __init__(self, size):
        """Initialize the Union-Find data structure with a given size."""
        self.parent = list(range(size))
        self.rank = [1] * size
        self.count = size

    def find(self, p):
        """Find the root of the set containing element p with path compression."""
        if self.parent[p] != p:
            self.parent[p] = self.find(self.parent[p])
        return self.parent[p]

    def union(self, p, q):
        """Union the sets containing elements p and q with union by rank."""
        root_p = self.find(p)
        root_q = self.find(q)

        if root_p == root_q:
            return

        if self.rank[root_p] > self.rank[root_q]:
            self.parent[root_q] = root_p
        elif self.rank[root_p] < self.rank[root_q]:
            self.parent[root_p] = root_q
        else:
            self.parent[root_q] = root_p
            self.rank[root_p] += 1
        self.count -= 1

def count_connected_components(edges):
    """Count the number of connected components in the graph represented by edges."""
    if not edges:
        return 0

    max_node = max(max(edge) for edge in edges)
    uf = UnionFind(max_node + 1)

    for u, v in edges:
        uf.union(u, v)

    isolated_nodes = set(range(1, max_node + 1)) - set(node for edge in edges for node in edge)

    return uf.count - len(isolated_nodes)

