class InventoryManager:
    def __init__(self):
        self.inventory = {}

    def add_item(self, item_name, quantity):
        """
        Adds the specified quantity of the item to the inventory.
        If the item already exists, the quantity is updated.

        :param item_name: The name of the item to add.
        :param quantity: The quantity of the item to add.
        """
        if item_name in self.inventory:
            self.inventory[item_name] += quantity
        else:
            self.inventory[item_name] = quantity

    def remove_item(self, item_name, quantity):
        """
        Removes the specified quantity of the item from the inventory.
        If the quantity of the item becomes zero or negative, it is removed from the inventory.

        :param item_name: The name of the item to remove.
        :param quantity: The quantity of the item to remove.
        """
        if item_name in self.inventory:
            self.inventory[item_name] -= quantity
            if self.inventory[item_name] <= 0:
                del self.inventory[item_name]

    def get_quantity(self, item_name):
        """
        Returns the current quantity of the specified item in the inventory.
        If the item does not exist, returns 0.

        :param item_name: The name of the item to check.
        :return: The quantity of the item in the inventory.
        """
        return self.inventory.get(item_name, 0)
