def binary_search_recursive(arr, target):
    """
    Perform a binary search on a sorted array to find the target value.

    :param arr: List of sorted integers
    :param target: Integer target value to search for
    :return: Index of the target if found, otherwise -1
    """
    def search(low, high):
        if low > high:
            return -1
        mid = (low + high) // 2
        if arr[mid] == target:
            return mid
        elif arr[mid] < target:
            return search(mid + 1, high)
        else:
            return search(low, mid - 1)

    return search(0, len(arr) - 1)
