import random

def random_haiku():
    """
    Generates a random haiku poem with a 5-7-5 syllable structure using predefined lists of words categorized by syllable count.

    Returns:
        str: A string representing the generated haiku poem.
    """
    # Predefined lists of words based on their syllable count
    one_syllable_words = ["dog", "cat", "run", "sit", "fly", "hat", "bat", "mat"]
    two_syllable_words = ["bird", "tree", "road", "beach", "heart", "love", "moon", "star"]
    three_syllable_words = ["children", "mountain", "river", "forest", "happiness", "friendship", "universe", "ocean"]

    # Function to select a random word with a specific syllable count
    def select_word(syllables):
        if syllables == 1:
            return random.choice(one_syllable_words)
        elif syllables == 2:
            return random.choice(two_syllable_words)
        elif syllables == 3:
            return random.choice(three_syllable_words)
        else:
            raise ValueError("This function only supports syllable counts of 1, 2, or 3.")

    # Function to create a line with a given syllable count
    def create_line(syllable_count):
        line = []
        while sum(line) < syllable_count:
            syllables_to_add = random.choice([1, 2, 3])
            if sum(line) + syllables_to_add <= syllable_count:
                line.append(syllables_to_add)
        words = [select_word(syllables) for syllables in line]
        return " ".join(words)

    # Generate the haiku with the 5-7-5 syllable structure
    haiku_lines = [create_line(5), create_line(7), create_line(5)]
    haiku = "\n".join(haiku_lines)
    return haiku

# Example usage for testing
print(random_haiku())
