def swap_keys_values(original_dict: dict) -> dict:
    """
    Constructs a new dictionary with keys and values swapped from the original dictionary.
    If multiple keys share the same value, the corresponding value in the new dictionary
    is a list of all those keys.

    :param original_dict: A dictionary with unique keys.
    :return: A new dictionary with keys and values swapped.
    """
    swapped_dict = {}
    for key, value in original_dict.items():
        if value in swapped_dict:
            if isinstance(swapped_dict[value], list):
                swapped_dict[value].append(key)
            else:
                swapped_dict[value] = [swapped_dict[value], key]
        else:
            swapped_dict[value] = key
    return swapped_dict
