def is_prime(n):
    """Check if a number is a prime number."""
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

def find_largest_prime(numbers):
    """Find the largest prime number in a list of numbers."""
    largest_prime = None
    for number in numbers:
        if is_prime(number):
            if largest_prime is None or number > largest_prime:
                largest_prime = number
    return largest_prime
