def classify_numbers(numbers):
    """
    Classifies numbers into even, odd, and prime categories and returns the counts.

    :param numbers: List of integers to be classified.
    :return: Dictionary with counts of even, odd, and prime numbers.
    """
    def is_prime(num):
        """Check if a number is prime."""
        if num <= 1:
            return False
        if num == 2:
            return True
        if num % 2 == 0:
            return False
        for i in range(3, int(num**0.5) + 1, 2):
            if num % i == 0:
                return False
        return True

    counts = {'even': 0, 'odd': 0, 'prime': 0}
    for number in numbers:
        if number % 2 == 0:
            counts['even'] += 1
        else:
            counts['odd'] += 1

        if is_prime(number):
            counts['prime'] += 1

    return counts
