def flatten_json(y):
    """
    Flattens a nested dictionary into a single-level dictionary with keys representing the path to each value.

    :param y: A dictionary representing a JSON object.
    :return: A flattened dictionary with dot-separated keys.
    """
    out = {}

    def flatten(x, name=''):
        if isinstance(x, dict):
            for a in x:
                flatten(x[a], name + a + '.')
        elif isinstance(x, list):
            for i, a in enumerate(x):
                flatten(a, name + str(i) + '.')
        else:
            out[name[:-1]] = x

    flatten(y)
    return out

