def convert_to_json(data):
    """
    Serializes a given Python dictionary into a JSON string.

    Args:
    data (dict): The dictionary to be serialized.

    Returns:
    str: A JSON string representation of the input dictionary.

    Raises:
    TypeError: If the dictionary contains unsupported data types.
    """
    if isinstance(data, dict):
        items = []
        for key, value in data.items():
            items.append(f'"{convert_to_json(key)}": {convert_to_json(value)}')
        return f"{{{', '.join(items)}}}"
    elif isinstance(data, list):
        items = []
        for item in data:
            items.append(convert_to_json(item))
        return f"[{', '.join(items)}]"
    elif isinstance(data, (str, bool)):
        if isinstance(data, bool):
            return f"{str(data).lower()}"
        return f'"{data}"'
    elif data is None:
        return "null"
    elif isinstance(data, (int, float)):
        return str(data)
    else:
        raise TypeError(f"Type {type(data)} not serializable")
