import json

def normalize_value(value):
    """
    Normalize a single value by converting it to lowercase, replacing underscores with spaces,
    and stripping leading and trailing whitespace if it is a string.
    """
    if isinstance(value, str):
        return value.lower().replace('_', ' ').strip()
    return value

def normalize_dict(d):
    """
    Recursively normalize a dictionary by applying normalization to all string values,
    including keys and values in nested dictionaries and lists.
    """
    normalized_dict = {}
    for key, value in d.items():
        if isinstance(value, dict):
            normalized_dict[normalize_value(key)] = normalize_dict(value)
        elif isinstance(value, list):
            normalized_dict[normalize_value(key)] = [normalize_value(item) for item in value]
        else:
            normalized_dict[normalize_value(key)] = normalize_value(value)
    return normalized_dict

def read_json_file(filename):
    """
    Read and parse a JSON file, returning the data as a Python dictionary.
    """
    with open(filename, 'r') as file:
        return json.load(file)

def write_json_file(data, filename):
    """
    Write a Python dictionary to a JSON file with pretty printing (indentation).
    """
    with open(filename, 'w') as file:
        json.dump(data, file, indent=4)

if __name__ == '__main__':
    input_file = 'input.json'
    output_file = 'normalized_output.json'

    data = read_json_file(input_file)
    normalized_data = normalize_dict(data)
