def sieve_of_eratosthenes(max_num):
    """
    Returns a set of prime numbers up to max_num using the Sieve of Eratosthenes algorithm.

    :param max_num: The upper limit for finding prime numbers.
    :return: A set of prime numbers up to max_num.
    """
    if max_num < 2:
        return set()

    is_prime = [True] * (max_num + 1)
    is_prime[0] = is_prime[1] = False

    for start in range(2, int(max_num**0.5) + 1):
        if is_prime[start]:
            for multiple in range(start*start, max_num + 1, start):
                is_prime[multiple] = False

    return {num for num, prime in enumerate(is_prime) if prime}

def filter_primes(numbers):
    """
    Filters the input list to return only the prime numbers.

    :param numbers: A list of integers.
    :return: A list of prime numbers from the input list.
    """
    if not numbers:
        return []

    max_num = max(numbers)
    primes_set = sieve_of_eratosthenes(max_num)

    return [num for num in numbers if num in primes_set]
