def longest_valid_parentheses(s: str) -> int:
    """
    Finds the length of the longest valid (well-formed) parentheses substring in the given string `s`.

    :param s: A string consisting of '(' and ')' characters.
    :return: The length of the longest valid parentheses substring.
    """
    max_length = 0
    stack = [-1]  # Initialize stack with base value -1

    for i, char in enumerate(s):
        if char == '(':
            stack.append(i)
        else:
            stack.pop()
            if not stack:
                stack.append(i)
            else:
                max_length = max(max_length, i - stack[-1])

    return max_length
