def is_valid_parentheses(s: str) -> bool:
    """
    Determines if the input string s containing only the characters '(', ')', '{', '}', '[' and ']'
    is valid. A string is valid if all opening brackets are closed by the same type of brackets
    in the correct order. An empty string is considered valid.

    :param s: The input string to be checked.
    :return: True if the string is valid, otherwise False.
    """
    matching_bracket = {')': '(', '}': '{', ']': '['}
    stack = []

    for char in s:
        if char in matching_bracket:
            top_element = stack.pop() if stack else '#'
            if matching_bracket[char] != top_element:
                return False
        else:
            stack.append(char)

    return not stack
