def convert_currency(amount, from_currency, to_currency):
    """
    Convert a given amount of money from one currency to another using predefined exchange rates.

    Parameters:
    - amount (float): The amount of money to be converted.
    - from_currency (str): The currency code of the source currency.
    - to_currency (str): The currency code of the target currency.

    Returns:
    - float: The converted amount rounded to two decimal places, or a string indicating that the exchange rate is not available.
    """
    # Define the exchange rates
    exchange_rates = {
        'USD': {'EUR': 0.85, 'GBP': 0.75},
        'EUR': {'USD': 1.18, 'GBP': 0.88},
        'GBP': {'USD': 1.33, 'EUR': 1.14}
    }

    # Direct conversion
    if from_currency == to_currency:
        return round(amount, 2)

    # Convert the amount using the defined exchange rates
    try:
        converted_amount = amount * exchange_rates[from_currency][to_currency]
        return round(converted_amount, 2)
    except KeyError:
        return "Exchange rate not available for the given currencies."
