class Polynomial:
    """
    A class to represent a polynomial function.
    The internal representation is a dictionary where keys are the exponents
    and values are the coefficients.
    """

    def __init__(self, coefficients=None):
        """
        Initialize a polynomial with a list of coefficients.
        Coefficients are provided in descending order of power.

        :param coefficients: A list of coefficients for the polynomial
        """
        if coefficients is None:
            self.terms = {}
        else:
            self.terms = {exp: coeff for exp, coeff in enumerate(coefficients[::-1]) if coeff != 0}

    def __add__(self, other):
        """
        Add two polynomials together.

        :param other: Another Polynomial object
        :return: A new Polynomial object representing the sum
        """
        result = self.terms.copy()
        for exp, coeff in other.terms.items():
            result[exp] = result.get(exp, 0) + coeff
        return Polynomial({exp: coeff for exp, coeff in result.items() if coeff != 0})

    def __sub__(self, other):
        """
        Subtract a polynomial from this one.

        :param other: Another Polynomial object
        :return: A new Polynomial object representing the difference
        """
        result = self.terms.copy()
        for exp, coeff in other.terms.items():
            result[exp] = result.get(exp, 0) - coeff
        return Polynomial({exp: coeff for exp, coeff in result.items() if coeff != 0})

    def __mul__(self, other):
        """
        Multiply two polynomials together.

        :param other: Another Polynomial object
        :return: A new Polynomial object representing the product
        """
        result = {}
        for exp1, coeff1 in self.terms.items():
            for exp2, coeff2 in other.terms.items():
                exp = exp1 + exp2
                result[exp] = result.get(exp, 0) + (coeff1 * coeff2)
        return Polynomial({exp: coeff for exp, coeff in result.items() if coeff != 0})

    def evaluate(self, x):
        """
        Evaluate the polynomial at a given point x.

        :param x: The value at which to evaluate the polynomial
        :return: The result of the evaluation
        """
        result = 0
        for exp, coeff in self.terms.items():
            result += coeff * (x ** exp)
        return result

    def __repr__(self):
        """
        String representation of the polynomial.

        :return: A string representing the polynomial
        """
        terms = sorted(self.terms.items(), reverse=True)
        parts = []
        for exp, coeff in terms:
            if coeff == 0:
                continue
            if coeff > 0 and parts:
                parts.append('+')
            if coeff == -1 and exp != 0:
                parts.append('-')
            elif coeff != 1 or exp == 0:
                parts.append(f'{coeff:.1f}' if coeff % 1 else f'{int(coeff)}')
            if exp == 0:
                continue
            if exp == 1:
                parts.append('x')
            else:
                parts.append(f'x^{exp}')
        return ' '.join(parts).replace('+ -', '- ')
