class Matrix:
    def __init__(self, data):
        """
        Initializes the matrix with the given 2D list `data`.
        Raises a `ValueError` if `data` is not a valid 2D list with consistent row lengths.
        """
        if not data or not all(len(row) == len(data[0]) for row in data):
            raise ValueError("Data must be a 2D list with consistent row lengths.")
        self.data = data
        self.rows = len(data)
        self.cols = len(data[0])

    def __add__(self, other):
        """
        Adds two matrices.
        Raises a `ValueError` if the matrices do not have the same dimensions.
        """
        if self.rows != other.rows or self.cols != other.cols:
            raise ValueError("Matrices must have the same dimensions for addition.")
        return Matrix([[self.data[i][j] + other.data[i][j] for j in range(self.cols)] for i in range(self.rows)])

    def __sub__(self, other):
        """
        Subtracts one matrix from another.
        Raises a `ValueError` if the matrices do not have the same dimensions.
        """
        if self.rows != other.rows or self.cols != other.cols:
            raise ValueError("Matrices must have the same dimensions for subtraction.")
        return Matrix([[self.data[i][j] - other.data[i][j] for j in range(self.cols)] for i in range(self.rows)])

    def __mul__(self, other):
        """
        Multiplies two matrices or a matrix by a scalar.
        Raises a `ValueError` if the matrices cannot be multiplied or if `other` is not a matrix or a scalar.
        """
        if isinstance(other, Matrix):
            if self.cols != other.rows:
                raise ValueError("Number of columns in first matrix must equal number of rows in second matrix for multiplication.")
            result_matrix = [
                [sum(self.data[i][k] * other.data[k][j] for k in range(self.cols)) for j in range(other.cols)]
                for i in range(self.rows)
            ]
            return Matrix(result_matrix)
        elif isinstance(other, (int, float)):
            return Matrix([[self.data[i][j] * other for j in range(self.cols)] for i in range(self.rows)])
        else:
            raise ValueError("Multiplication is only defined between two matrices or a matrix and a scalar.")

    def __repr__(self):
        """
        Returns a string representation of the matrix.
        """
        return '\n'.join([' '.join(map(str, row)) for row in self.data])
