class BookstoreInventory:
    def __init__(self):
        """Initialize the inventory dictionary."""
        self.inventory = {}

    def add_book(self, title, author, stock_count):
        """Add a new book to the inventory. Raise ValueError if the book already exists."""
        if title in self.inventory:
            raise ValueError("Book already exists in inventory.")
        self.inventory[title] = (author, stock_count)

    def remove_book(self, title):
        """Remove a book from the inventory. Raise KeyError if the book does not exist."""
        if title not in self.inventory:
            raise KeyError("Book not found in inventory.")
        del self.inventory[title]

    def search_book(self, title=None, author=None):
        """Search for a book by title or author. Return an empty dictionary if searching by title and the book does not exist.
        Return all books by the author if searching by author. Raise ValueError if neither title nor author is provided."""
        if title:
            return {title: self.inventory[title]} if title in self.inventory else {}
        elif author:
            return {k: v for k, v in self.inventory.items() if v[0] == author}
        else:
            raise ValueError("Either title or author must be provided for search.")

    def update_stock(self, title, stock_count):
        """Update the stock count of a book. Raise KeyError if the book does not exist."""
        if title not in self.inventory:
            raise KeyError("Book not found in inventory.")
        author, _ = self.inventory[title]
        self.inventory[title] = (author, stock_count)

    def list_books(self):
        """Return a dictionary of all books in the inventory."""
        return self.inventory
